import React, {Component} from "react";
import axios from 'axios';
import HeaderComponent from "./header.component";
import ItemBoxComponent from "./item-box.component";

export default class ItemViewComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {
            data:[]
        };

    }

    componentDidMount() {
        this.loadData();
    }

    async loadData(){
        axios.get('http://localhost:3000/items')
        .then(res =>{
            this.setState({
                data: res.data
            });
        });
    }


    itemDataBoxes(){
        return this.state.data.map((object) => {
            return (
              <ItemBoxComponent/>
            );
        });
    }

    render() {
        return(
            <div>
                <HeaderComponent/>
                <div id="item-view-wrapper" className="container-fluid pt-3">
                    <div className="row">
                        <div className="col-6">
                            <label><input type="checkbox"/> Music Apps</label>
                            &#x00A0;&#x00A0;&#x00A0;
                            <label><input type="checkbox"/> Games Apps</label>
                        </div>
                        <div className="col-6 text-right">
                            <input type="text" placeholder="Search Apps"/>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
