import React, {Component} from "react";
import axios from 'axios';
import './item-view.css';

export default class ItemViewComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {
            data:[]
        };
    }

    componentDidMount() {
        this.loadData();
    }

    async loadData(){
        axios.get('http://localhost:3000/items')
        .then(res =>{
            this.setState({
                data: res.data
            });
            setTimeout(()=>{
                this.loadData();
            }, 3000);
        });
    }

    tableData(){
        return this.state.data.map((object) => {
            return (
              <tr key={object.id}>
                  <td>{object.id}</td>
                  <td>{object.name}</td>
              </tr>
            );
        });
    }

    render() {
        return(
            <div id="item-view" className="container-fluid">
                <nav className="row text-white">
                    <img className="id" src={require('./logo.png')}/>
                    <span className="tagline">Tagline Here...</span>
                </nav>
            </div>
        );
    }
}
