import React, {Component} from "react";
import validator from 'validator';
import './login.css';

export default class LoginComponent extends Component{

    constructor(props) {
        super(props);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            username:'',
            usernameError:'',
            password: '',
            passwordError: '',
            responseError:''
        }
    }

    validateUsername() {
        if (validator.isEmpty(this.state.username)) {
            this.setState({
                username:{
                    error: 'Username is required'
                }
            });
        }
        return true;
    }

    validatePassword() {
        if (validator.isEmpty(this.state.password.value)) {
            this.setState({
                password:{
                    error: 'Password is required'
                }
            });
        }
        return true;
    }

    onChangeUsername(e){
        this.setState({
            username: {
                value:e.target.value
            }
        });
        this.validateUsername();
    }

    onChangePassword(e){
        this.setState({
            password: {
                value:e.target.value
            }
        });
        this.validatePassword();
    }

    onSubmit(e){

    }

    render() {
        return(
            <div id="login-component" className="container-fluid">
                <div className="card">
                    <div className="card-body">
                        <img src={require('./logo.png')}/>
                        <div className="text-danger">{this.state.responseError}</div>
                        <form>
                            <div className="form-group">
                                <input
                                    value={this.state.username.value}
                                    className={"form-control form-control-lg "+(this.state.username.error==='' ? "":"is-invalid")}
                                    type="text" placeholder="Username"
                                    onChange={this.onChangeUsername}
                                />
                                <div className="invalid-feedback">{this.state.username.error}</div>
                            </div>
                            <div className="form-group">
                                <input
                                    value={this.state.password.value}
                                    className={"form-control form-control-lg "+(this.state.password.error==='' ? "":"is-invalid")}
                                    type="password"
                                    placeholder="Password"
                                    onChange={this.onChangePassword}
                                />
                                <div className="invalid-feedback">{this.state.password.error}</div>
                            </div>
                            <div>
                                <button className="btn btn-primary btn-lg btn-block">Sign In</button>
                            </div>
                        </form>
                        <a href="#">Click here to registration</a>
                    </div>
                </div>
            </div>
        );
    }
}
