import React, {Component} from "react";
import axios from 'axios';

export default class ItemViewComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {
            data:[]
        };
    }

    componentDidMount() {
        this.loadData();
    }

    async loadData(){
        axios.get('http://localhost:3000/items')
        .then(res =>{
            this.setState({
                data: res.data
            });
            setTimeout(this.loadData, 3000);
        });
    }

    tableData(){
        return this.state.data.map((object) => {
            return (
              <tr key={object.id}>
                  <td>{object.id}</td>
                  <td>{object.name}</td>
              </tr>
            );
        });
    }

    render() {
        return(
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                    </tr>
                </thead>
                <tbody>
                    {this.tableData()}
                </tbody>
            </table>
        );
    }
}
