import React, {Component} from "react";
import validator from 'validator';
import './login.css';

export default class LoginComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {
            username:{
                value:'',
                error:''
            },
            password:{
                value:'',
                error:''
            },
            responseError:''
        }
    }

    validateUsername(username) {
        if (validator.isEmpty(username)) {
            return 'Username is required';
        }
        return false;
    }

    validatePassword(password) {
        if (validator.isEmpty(password)) {
            return 'Password is required';
        }
        return false;
    }


    render() {
        return(
            <div id="login-component" className="container-fluid">
                <div className="card">
                    <div className="card-body">
                        <img src={require('./logo.png')}/>
                        <div className="text-danger">{this.state.responseError}</div>
                        <form>
                            <div className="form-group">
                                <input value={this.state.username.value} className="form-control form-control-lg" type="text" placeholder="Username"/>
                                <div className="invalid-feedback">{this.state.username.error}</div>
                            </div>
                            <div className="form-group">
                                <input value={this.state.password.value} className="is-invalid form-control form-control-lg" type="password" placeholder="Password"/>
                            </div>
                            <div>
                                <button className="btn btn-primary btn-lg btn-block">Sign In</button>
                            </div>
                        </form>
                        <a href="#">Click here to registration</a>
                    </div>
                </div>
            </div>
        );
    }
}
