import React, {Component} from "react";
import axios from 'axios';
import HeaderComponent from "./header.component";
import ItemBoxComponent from "./item-box.component";
import './item-view.css';

export default class ItemViewComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {
            data:[]
        };

    }

    componentDidMount() {
        this.loadData();
        document.getElementById('item-view-wrapper').style.height = (window.innerHeight - 71)+'px';
    }

    async loadData(){
        axios.get('http://localhost:3000/items')
        .then(res =>{
            this.setState({
                data: res.data.data
            });
        });
    }


    itemDataBoxes(){
        return this.state.data.map((object) => {
            return (
              <ItemBoxComponent item={object}/>
            );
        });
    }

    render() {
        return(
            <div>
                <HeaderComponent/>
                <div id="item-view-wrapper" className="container-fluid pt-3">
                    <div className="row">
                        <div className="col-6">
                            <label>
                                <input
                                    onChange={this.onChangeCategory}
                                    checked={this.state.category===''}
                                    value="Music"
                                    type="radio"
                                    name="category"
                                />
                                Music
                            </label>
                            <label>
                                <input
                                    onChange={this.onChangeCategory}
                                    checked={this.state.category==='Music'}
                                    value="Music"
                                    type="radio"
                                    name="category"
                                />
                                Music
                            </label>
                            &#x00A0;&#x00A0;&#x00A0;
                            <label>
                                <input
                                    onChange={this.onChangeCategory}
                                    checked={this.state.category==='Game'}
                                    value="Game"
                                    type="radio"
                                    name="category"
                                />
                                Game
                            </label>
                        </div>
                        <div className="col-6 text-right">
                            <input type="text" placeholder="Search Apps"/>
                        </div>
                    </div>
                    <div>
                        {this.itemDataBoxes()}
                    </div>
                </div>
            </div>
        );
    }
}
