import React, {Component} from "react";
import axios from 'axios';
import HeaderComponent from "./header.component";
import ItemBoxComponent from "./item-box.component";
import './item-view.css';

export default class ItemViewComponent extends Component{

    constructor(props) {
        super(props);

        this.onChangeCategory = this.onChangeCategory.bind(this);
        this.onChangeName = this.onChangeName.bind(this);

        this.state = {
            data:[],
            category:'',
            name:'',
            page:1,
            totalPageCount:0,
            isFirst: true,
            isLast: true,
        };
    }

    onChangeCategory(e){
        this.setState({
            category: e.target.value
        });
    }

    onChangeName(e){
        this.setState({
            name: e.target.value
        });
    }

    componentDidMount() {
        this.loadData();
        document.getElementById('item-view-wrapper').style.height = (window.innerHeight - 71)+'px';
    }

    async loadData(){
        axios.get('http://localhost:3000/items')
        .then(res =>{
            this.setState({
                data: res.data.data
            });
        });
    }


    itemDataBoxes(){
        return this.state.data.map((object) => {
            return (
              <ItemBoxComponent item={object}/>
            );
        });
    }


    pageButtons(){

        const pages = [];

        for(let i=0; i<this.state.totalPageCount; i++){
            pages.push(i+1);
        }

        return pages.map((page) => {
            return (
                <li className="page-item"><button onClick={this.onPageChange} className="page-link">1</button></li>
            );
        });
    }

    render() {
        return(
            <div>
                <HeaderComponent/>
                <div id="item-view-wrapper" className="container-fluid pt-3">
                    <div className="row">
                        <div className="col-6">
                            <label>
                                <input
                                    onChange={this.onChangeCategory}
                                    checked={this.state.category===''}
                                    value=""
                                    type="radio"
                                    name="category"
                                />&#x00A0;
                                All
                            </label>
                            &#x00A0;&#x00A0;&#x00A0;&#x00A0;&#x00A0;&#x00A0;
                            <label>
                                <input
                                    onChange={this.onChangeCategory}
                                    checked={this.state.category==='Music'}
                                    value="Music"
                                    type="radio"
                                    name="category"
                                />&#x00A0;
                                Music
                            </label>
                            &#x00A0;&#x00A0;&#x00A0;&#x00A0;&#x00A0;&#x00A0;
                            <label>
                                <input
                                    onChange={this.onChangeCategory}
                                    checked={this.state.category==='Game'}
                                    value="Game"
                                    type="radio"
                                    name="category"
                                />&#x00A0;
                                Game
                            </label>
                        </div>
                        <div className="col-6 text-right">
                            <input
                                type="text"
                                value={this.state.name}
                                onChange={this.onChangeName}
                                placeholder="Search Apps"
                            />
                        </div>
                    </div>
                    <div>
                        {this.itemDataBoxes()}
                    </div>
                    <div>
                        <ul className="pagination float-right">
                            <li className={"page-item "+ (this.state.isFirst ? 'd-none': '')}><button onClick={this.onPageChange} className="page-link" value={this.state.page - 1}>Previous</button></li>

                            <li className={"page-item "+ (this.state.isLast ? 'd-none': '')}><button onClick={this.onPageChange} className="page-link" value={this.state.page + 1}>Previous</button></li>
                        </ul>
                        <div className="clear"></div>
                    </div>
                </div>
            </div>
        );
    }
}
